+    if (!afterMap.has(path)) {
+      deleted++
+      items.push({ path, type: 'deleted', before: b, after: null })
+    }
+  }
+
+  items.sort((x, y) => x.path.localeCompare(y.path))
+  return { added, modified, deleted, items }
