{"output":"import { readFileText } from '../utils/fs-io'
import { parseUnifiedDiffToSides } from '../utils/diff'

export async function getWorkspaceDiff(
  root: FileSystemDirectoryHandle,
  path: string,
  events: readonly any[]
): Promise<{ original: string; modified: string }> {
  let modified = ''
  try { modified = await readFileText(root, path) } catch {}

  // Try to find a recent FileChange with a diff for baseline
  let original = ''
  try {
    for (let i = events.length - 1; i >= 0; i--) {
      const ev = events[i] as any
      if (ev && ev.type === 'FileChange' && ev.path === path && ev.diff) {
        const sides = parseUnifiedDiffToSides(ev.diff)
        original = sides.original
        break
      }
    }
  } catch {}

  return { original, modified }
}

